import React, { useEffect,useState} from "react";


import Header from "../../../../components/Header";
import SideBar from "../../../../components/SideBar";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogTitle from "@mui/material/DialogTitle";


import DialogContent from "@mui/material/DialogContent";

import { MdAdd } from "react-icons/md";
import CompanyLogoFile from "../../../../components/CompanyLogoFile";


import StatePolicies from "../StatePolicies";
import PoliciesTable from "../PoliciesTable";
import * as api from "../PoliciesApi"
import Policiesform from "../PoliciesForm";
import { Link } from "react-router-dom";

const PoliciesView = () => {
  const {
   formData,setFormData,
    policies,
    setPolicies,
    open,
    setOpen,
    recDelete,
    setRecDelete,
    
  } = StatePolicies();

 

  
  const handleClose = () => {
    setOpen(false);
  };

 

  useEffect(() => {
    loadPolicies();
  }, []);

  const loadPolicies = async () => {
    const response = await api.loadPolicies()
      setPolicies(response);
  };


  const handleDelete = async () => {
    await api.deletePolicies(recDelete)
    loadPolicies()
  };

  useEffect(() => {
    if (recDelete !== "") {
      handleDelete()
      setRecDelete("")
    }
  })

  console.log(formData)
  const [menu, setMenu] = useState(false);

  return (
    <div>
    <div id="header-container" className="header-container">
        <CompanyLogoFile />
        <Header menu={menu} setMenu={setMenu} />
      </div>
      <div className="dashboard-container">
        <SideBar menu={menu} setMenu={setMenu} />
      <div className="head-foot-part">
        <section>
          <div
            className="above-table"
            style={{ display: "flex", justifyContent: "space-between" }}
          >
            <div style={{marginTop:"60px",width:'150px'}}>
            <div style={{fontSize:"1.4rem",width:'500px',display:'flex'}}>
            <div style={{paddingRight:'10px'}}>
            <img src="data:image/png;base64," alt="Dashboard"/>
          </div>
          <div style={{padding:'2px'}}>
            <span style={{color:'black',fontWeight:'bold'}}> 
          <Link to="/HRDashboard" style={{ textDecoration: 'none', color: 'inherit' }}>
           Dashboard </Link> / 
            Organisation /</span> 
            <span style={{color:'black'}}> Policies</span>
          </div>
          </div>
             
            </div>
          </div>
          <PoliciesTable policies={policies} setRecDelete={setRecDelete} setOpen={setOpen} />
          <div>
            <Dialog open={open} onClose={handleClose}>
              <DialogTitle id="form-header-popup">
                Add Policies
              </DialogTitle>
              <DialogContent>
               <Policiesform formData={formData} setFormData={setFormData} setOpen={setOpen}/>
              </DialogContent>
            </Dialog>
          </div>
        </section>
      </div>
    </div>
  </div>
  );
};

export default PoliciesView;
